import axios from "axios"
import { useState } from "react"

const initialRequestInfo = {
    error: null,
    data: null,
    loading: false,
};

export default function useApi(config) {
    const [requestInfo, setRequestInfo] = useState(initialRequestInfo)

    function call(){
        setRequestInfo({
            ...initialRequestInfo,
            loading: true,
        })
        axios(config);

    }

    return [
        call,
        requestInfo
    ]
}
