import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import "./Form.css";
import axios from "axios";
import { useEffect } from "react/cjs/react.development";
const initialValue  = {
  title: " ",
  url: " ",
  imageUrl: " ",
  price: 0.0,
};
const PromotionForm = ({ id }) => {
  const [values, setValues] = useState({ initialValue });
  const history = useHistory();
  console.log(id);

  useEffect(()=>{
    if(id){
      axios.get("http://localhost:5000/promotions/${id}")
      .then((response)=> {
        console.log(response.data);
        setValues(response.data)
      })
    }
  });

  function onChange(ev) {
    const { name, value } = ev.target;

    console.log(values);

    setValues({ ...values, [name]: value });
  }

  function onSubmit(ev) {
    ev.preventDefault();
    axios.post("http://localhost:5000/promotions", values).then((response) => {
      history.push("/");
    });
  }

  return (
    <div>
      <h1>Promo Show</h1>
      <h2>Nova Promoção</h2>
      <form onSubmit={onSubmit}>
        {/* dados do db.json */}
        <div className="promotion-form__group">
          <label htmlFor="title">Título</label>
          <input id="title" name="title" type="text" onChange={onChange} />
        </div>

        <div className="promotion-form__group">
          <label htmlFor="url">Link</label>
          <input id="url" name="link" type="text" onChange={onChange} />
        </div>

        <div className="promotion-form__group">
          <label htmlFor="imageUrl">Imagem (URL)</label>
          <input id="imageUrl" name="imageUrl" type="text" onChange={onChange}/>
        </div>

        <div className="promotion-form__group">
          <label htmlFor="price">Preço</label>
          <input id="price" name="price" type="number" onChange={onChange} />
        </div>

        <div>
          <button type="submit">Salvar</button>
        </div>
      </form>
    </div>
  );
};

export default PromotionForm;
